mapper.select:
SELECT 	f.*, m.name writerName 
FROM 	freeboard f INNER JOIN member m ON f.writer = m.memberId 
WHERE 	boardNo = #{boardNo}

mapper.update:
UPDATE 	freeboard 
SET 	likes = likes + 1 
WHERE 	boardNo = #{boardNo}

mapper.select:
SELECT 	likes 
FROM 	freeboard 
WHERE 	boardNo = #{boardNo}

SELECT 	COUNT(*) 
FROM 	fblikes 
WHERE 	boardNo = #{boardNo} 
AND 	memberId = #{memberId}

mapper.update:
UPDATE 	freeboard 
SET 	likes = likes - 1 
WHERE 	boardNo = #{boardNo}

mapper.delete:
DELETE 
FROM 	fblikes 
WHERE 	boardNo = #{boardNo} 
AND 	memberId = #{memberId}
