mapper.select:
SELECT 	COUNT(*) 
FROM 	point 
WHERE 	member = #{memberId} 
AND 	content = #{pointContent} 
AND 	DATE_FORMAT(accrDate, "%Y-%m-%d") = CURDATE()

SELECT 	COUNT(*) 
FROM 	point 
WHERE 	member = #{member} 
AND 	content = #{content}

mapper.update:
UPDATE 	member 
SET 	grade = #{memberGrade} 
WHERE 	memberId = #{member}
