mapper.update:
UPDATE 	classboard 
SET 	views = views + 1 
WHERE 	boardNo = #{boardNo}

UPDATE 	classboard 
SET 	title = #{title}, content = #{content}, category = #{category} 
WHERE 	boardNo = #{boardNo}

UPDATE 	classboard 
SET 	likes = likes + 1 
WHERE 	boardNo = #{boardNo}

mapper.select:
SELECT 	likes 
FROM 	classboard 
WHERE 	boardNo = #{boardNo}

SELECT 	COUNT(*) 
FROM 	cblikes 
WHERE 	boardNo = #{boardNo} 
AND 	memberId = #{memberId}

mapper.update:
UPDATE 	classboard 
SET 	likes = likes - 1 
WHERE 	boardNo = #{boardNo}

mapper.delete:
DELETE 
FROM 	cblikes 
WHERE 	boardNo = #{boardNo} 
AND 	memberId = #{memberId}
