mapper.update:

		UPDATE member
            SET
            group1 = #{groupNo}
            where memberId = #{memberId}


		UPDATE member
            SET
            group2 = #{groupNo}
            where memberId = #{memberId}


		UPDATE member
            SET
            group3 = #{groupNo}
            where memberId = #{memberId}

mapper.select:
SELECT 	COUNT(*) 
FROM 	groupsearch 
WHERE 	groupNo = #{groupNo} 
AND		memberId = #{memberId}
