mapper.update:
UPDATE 	fbcomment 
SET 	content = #{content} 
WHERE 	commentNo = #{commentNo}

UPDATE 	fbcomment 
SET 	likes = likes + 1 
WHERE 	commentNo = #{commentNo}

mapper.select:
SELECT 	likes 
FROM 	fbcomment 
WHERE 	commentNo = #{commentNo}

SELECT 	COUNT(*) 
FROM 	fbcommentlikes 
WHERE 	commentNo = #{commentNo} 
AND 	memberId = #{memberId}

mapper.update:
UPDATE 	fbcomment 
SET 	likes = likes - 1 
WHERE 	commentNo = #{commentNo}

mapper.delete:
DELETE 
FROM 	fbcommentlikes 
WHERE 	commentNo = #{commentNo} 
AND 	memberId = #{memberId}
