mapper.select:
SELECT 	* 
FROM 	classboard 
WHERE 	lectureNo = #{lectureNo} 
AND 	boardNo < #{maxNo}
AND 	category = 'TIL' 
AND 	writer = #{memberId} 
ORDER BY boardNo DESC LIMIT 5

SELECT 	COUNT(boardNo) 
FROM 	classboard 
WHERE 	lectureNo = #{lectureNo} 
AND 	category = 'TIL' 
AND 	writer = #{memberId}

SELECT 	MAX(boardNo) 
FROM 	classboard 
WHERE 	lectureNo = #{lectureNo} 
AND 	category = 'TIL' 
AND 	writer = #{memberId}

SELECT 	* 
FROM 	classboard 
WHERE 	lectureNo = #{lectureNo} 
AND 	category = 'TIL' 
AND 	writer = #{memberId} 
AND 	title LIKE #{keyword} OR content LIKE #{keyword} 
ORDER BY boardNo DESC

SELECT 	*, m.name writerName 
FROM 	classboard c INNER JOIN member m ON c.writer = m.memberId 
AND 	c.lectureNo = #{lectureNo} 
AND 	c.boardNo < #{maxNo}
ORDER BY boardNo DESC LIMIT 5

SELECT 	*, m.name writerName 
FROM 	classboard c INNER JOIN member m ON c.writer = m.memberId 
AND 	c.lectureNo = #{lectureNo} 
AND 	c.boardNo < #{maxNo}
AND 	c.category = #{viewCategory} 
ORDER BY boardNo DESC LIMIT 5

SELECT 	COUNT(boardNo) 
FROM 	classboard 
WHERE 	lectureNo = #{lectureNo}

SELECT 	COUNT(boardNo) 
FROM 	classboard 
WHERE 	lectureNo = #{lectureNo} 
AND 	category = #{viewCategory}

SELECT 	MAX(boardNo) 
FROM 	classboard 
WHERE 	lectureNo = #{lectureNo}

SELECT 	MAX(boardNo) 
FROM 	classboard 
WHERE 	lectureNo = #{lectureNo} 
AND 	category = #{viewCategory}

SELECT 	*, m.name writerName 
FROM 	classboard c INNER JOIN member m ON c.writer = m.memberId 
AND 	c.boardNo = #{boardNo}

mapper.update:
UPDATE 	classboard 
SET 	views = views + 1 
WHERE 	boardNo = #{boardNo}

UPDATE 	classboard 
SET 	title = #{title}, content = #{content}, category = #{category} 
WHERE 	boardNo = #{boardNo}

UPDATE 	classboard 
SET 	likes = likes + 1 
WHERE 	boardNo = #{boardNo}

