mapper.select:
SELECT COUNT(*)
FROM member
WHERE memberId = #{memberId}

SELECT memberId, passwd, name, dateOfBirth, gender, tel, 
zipcode, address, addressDetail, email, regDate, lectureNo
FROM member
WHERE memberId = #{memberId}
AND passwd = #{passwd}

mapper.update:
UPDATE 	member
SET 	passwd = #{passwd}, name = #{name}, email = #{email}, 
		dateOfBirth = #{dateOfBirth}, gender = #{gender}, tel = #{tel}, 
		zipcode = #{zipcode}, address = #{address}, addressDetail = #{addressDetail} 
WHERE 	memberId = #{memberId}

mapper.delete:
DELETE 
FROM member
WHERE memberId = #{memberId}
AND passwd = #{passwd}

mapper.select:
SELECT 	memberId 
FROM 	member 
WHERE 	lectureNo = #{lectureNo}
